import { styled } from "styled-components";

export const StyledTetrisWrapper = styled.div`
    width: 100%;
    height: 100%;
    overflow: hidden;
    color: white;
`

export const StyledTetris = styled.div`
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;

    /* game stats & button */
    aside {
        background-color: yellow;
        height: 100%;
    }
`
