import React from 'react';

import styled, { css } from 'styled-components';
import { TETROMINOS } from '../../constants';


const Cell = ({ type, isGhost }) => {
    return isGhost ? <StyledCell ghost={'true'} type={type} color={TETROMINOS[type].color} /> : <StyledCell type={type} color={TETROMINOS[type].color} />;
}

// export default Cell;
export default React.memo(Cell);

const StyledCell = styled.div`
    display: inline;
    width: auto;


    border: 2px solid;
    border-bottom-color: rgba(${props => props.color}, 0.1);
    border-right-color: rgba(${props => props.color}, 1);
    border-top-color: rgba(${props => props.color}, 1);
    border-left-color: rgba(${props => props.color}, 0.3);
    border-radius: 4px;

    ${props =>
    props.ghost &&
    css`
      background-color: rgba(${props => props.color}, 0.3);
      border-width: 1px;
      border-color: rgba(${props => props.color}, 0.5);
      box-shadow: 0px 0px 4px rgba(${props => props.color}, 0.7);
    `}
`
