import { useEffect } from "react";
import { selectColor } from "../utill.js/selectColor";

export const useGetBlock = (shapeRef, grid, setGrid) => {
    const shape = shapeRef.current;
    const position = {
        row: 0,
        col: (10 / 2)
    }
    let color;
    useEffect( () => {
        if(shape === null) return;
        // eslint-disable-next-line react-hooks/exhaustive-deps
        color = selectColor(shape);
        const newGrid = [...grid];
        for(let row=0; row<shape.length; row++) {
            for(let col=0; col<shape[row].length; col++) {
                if(shape[row][col] === 1) {
                    const x = position.row + row;
                    const y = position.col + col;
                    newGrid[x][y] = 1;
                }
            }
        }
        setGrid(newGrid);
        console.log(grid);
    }, [shape])

    return [grid, color];
}
