import { useState, useEffect } from "react";
import { useCountDown } from "../hooks/useCountDown";
import Board from "./Board";
import "../css/Tetris.css";


const Tetris = () => {
    const [isStart, setIsStart] = useState(false);
    const countDown = useCountDown(isStart);
    console.log(typeof countDown);
    const onClick = () => setIsStart(true);

    return(
        <div className="Tetris">
            {isStart && countDown > 0 && <span className="count-down">{countDown}</span>}
            <Board />
            {isStart ? null : <button onClick={onClick} className="start-button">Start</button>}
        </div>
    );
}

export default Tetris;
