import React from 'react';

import styled, { css } from 'styled-components';
import { TETROMINOS } from '../../constants';


const Cell = ({ type, isGhost }) => {
    return isGhost ? <StyledCell ghost={isGhost ? 'true' : 'false'} type={type} color={TETROMINOS[type].color} /> : <StyledCell type={type} color={TETROMINOS[type].color} />;
}

// export default Cell;
export default React.memo(Cell);

const StyledCell = styled.div`
    display: inline;
    width: auto;

    ${props => 
        props.ghost
            ? css` //ghost
                border: 1px solid;
                border-radius: 4px;
                border-color: rgba(${props=>props.color}, 0.2);
                background-color: aliceblue;
            ` 
            : css` //player
                background-color: rgba(${props => props.color}, 0.8);
                border: ${props => (props.type === 0 ? '0px solid' : '2px solid')};
                border-radius: 4px;
                border-bottom-color: rgba(${props => props.color}, 0.1);
                border-right-color: rgba(${props => props.color}, 1);
                border-top-color: rgba(${props => props.color}, 1);
                border-left-color: rgba(${props => props.color}, 0.3);
            ` 
    }

    /* 일반 셀 스타일
    background-color: rgba(${props => props.color}, 0.8);
    border: ${props => (props.type === 0 ? '0px solid' : '2px solid')};

    border-radius: 4px;

    고스트 셀 스타일
    ${props =>
        props.ghost &&
        css`
        background-color: transparent;
        border: ${props => (props.type === 0 ? '0px solid white' : '2px solid white')};
        border-bottom-color: rgba(${props => props.color}, 0.1);
        border-right-color: rgba(${props => props.color}, 1);
        border-top-color: rgba(${props => props.color}, 1);
        border-left-color: rgba(${props => props.color}, 0.3);
        border-radius: 4px;
        `} */
`
