import { useState, useEffect } from "react";

import { createStage, checkCollision } from "../../gameHelpers";

export const useStage = (player, setPlayer, resetPlayer) => {
    const [stage, setStage] = useState(createStage());
    const [rowsCleared, setRowsCleared] = useState(0);     


    useEffect( () => {
        setRowsCleared(0);

        const calculateGhost = (stage) => {

            const clonedPlayer = JSON.parse(JSON.stringify(player));

            while(true) {
                if(clonedPlayer.pos.y < 20) {
                    console.log(stage);
                    // if(stage[clonedPlayer.pos.y][clonedPlayer.pos.x][0] === [0, 'clear']) { //bug
                    //     clonedPlayer.pos.y += 1;
                    // } else {
                    //     break;
                    // }
                    break;
                }
                else {
                    break;
                }
            }
            clonedPlayer.pos.y--;
            clonedPlayer.ghost = clonedPlayer.tetromino.map((row, y) =>
                row.map((value, x) => (value !== 0 ? 'G' : 0))
            );
            setPlayer(clonedPlayer);           
        }

        const sweepRows = newStage => 
            newStage.reduce((ack, row) => {
                // 각 줄을 순회하며 특정 라인의 모든 셀이 채워져 있는 경우
                if(row.findIndex(cell => cell[0] === 0) ===-1) {
                    setRowsCleared(prev => prev + 1);
                    ack.unshift(new Array(newStage[0].length).fill([0, 'clear']));
                    return ack;
                }
                ack.push(row);
                return ack;
            }, []);



        const updateStage = prevStage => {
            const newStage = prevStage.map(row => row.map(cell => (cell[1] === 'clear' ? [0, 'clear'] : cell)));

            player.tetromino.forEach((row, y) => {
                row.forEach((value, x) => {
                    if(value !== 0) {
                        newStage[y + player.pos.y][x + player.pos.x] = [value, `${player.collide ? 'merged' : 'clear'}`];
                    }
                })
            });

            // Ghost set
            calculateGhost(prevStage);


            //충돌 감지시 새 블럭 생성
            if(player.collide) {  
                resetPlayer() 
                return sweepRows(newStage);
            }
            return newStage;
        }

        // 블럭 그림
        setStage(prev => updateStage(prev));

    }, [player, setPlayer, resetPlayer]);

    return [stage, setStage, rowsCleared];
}
