import { useState, useEffect } from "react"
import { initQueue } from "../utill.js/initQueue";

export const useQueue = () => {
    const [blockQueue, setBlockQueue] = useState(initQueue());


    const pop = () => {
        return blockQueue.pop();
    }

    useEffect( () => {
        if(blockQueue.length < 1) {
            setBlockQueue(initQueue());
        }
    }, [blockQueue])
    return [blockQueue, setBlockQueue, pop];
}
