import "../css/Game.css";

// React
import { useCallback, useEffect, useRef, useState } from "react";

// Components
import Tetris from "./Tetris/Tetris";
import RankingPage from "./Rank";



const Game = () => {
    //effect
    const tetrisRef = useRef(null);


    const [isStart, setIsStart] = useState(false);
    const onClickSingle = () => {
        if(isStart === false){
            setIsStart(true);
        }else{
            setIsStart(false);
        }
    }
    const [isRank, setIsRank] = useState(false);
    const onClickRank = () => {
        if(isRank === false){
            setIsRank(true);
        }else{
            setIsRank(false);
        }
    }

    const textGlow = useCallback( text => {
        let blurValue = 30;
        let increasing = false;

        if(blurValue >= 30) {
            increasing = false;
        } else if(blurValue <= 10) {
            increasing = true;
        }
        if(increasing) {
            blurValue += 1;
        } else {
            blurValue -= 1;
        }
        text.style.textShadow = `0 0 ${blurValue}px rgb(255, 0, 0)`;
    })
    useEffect(() => {
        setInterval(() => {
            textGlow(tetrisRef.current);
        }, 200)
    }, [])


    return(
        <div className="Game">
            {isStart ? <Tetris setIsStart ={onClickSingle}/> : 
            isRank ? <RankingPage setIsRank={onClickRank}/> :
            (<div id="Home">
                <h1 id="Tetris">TETRIS</h1>
                <div className="button-list">
                    <button onClick={onClickSingle}>Single Play</button>
                    <button onClick={onClickRank}>Ranking</button>
                    <button>Setting</button>
                </div>
            </div>)}
        </div>
    )
}
export default Game;
