import React from 'react';

import styled from 'styled-components';
import { TETROMINOS } from '../../constants';


const Cell = ({ type, ghost }) => {
    return(
        <StyledCell type={type} color={TETROMINOS[type].color} ghost={ghost}>            
        </StyledCell>
    );
}

export default React.memo(Cell);

const StyledCell = styled.div`
    display: inline;
    width: auto;
    /* background-color: rgba(${props => props.color}, 0.8); */
    background-color: ${props => props.ghost ? `rgba(${props.color}, 0.5)` : `rgba(${props.color}, 0.8)`};
    border: ${props => props.type === 0 ? '0px solid black' : '2px solid black'};
    border-bottom-color: rgba(${props => props.color}, 0.1);
    border-right-color: rgba(${props => props.color}, 1);
    border-top-color: rgba(${props => props.color}, 1);
    border-left-color: rgba(${props => props.color}, 0.3);
    border-radius: 4px;
`
