import { StyledTetrisWrapper, StyledTetris } from "../../css/StyledTetris";

import { useState } from "react";

// Components
import Stage from "./Stage";
import Display from "./Display";
import StartButton from "./StartButton";

// Custom Hooks
import { usePlayer } from "../../hooks/tetris/usePlayer";
import { useStage } from "../../hooks/tetris/useStage";
import { useInterval } from "../../hooks/tetris/useInterval";
import { useStats } from "../../hooks/tetris/useStats";

import { checkCollision, createStage } from "../../gameHelpers";

const Tetris = () => {
    const [dropTime, setDropTime] = useState(null);
    const [gameOver, setGameOver] = useState(false);

    const [player, updatePlyerPos, resetPlayer, playerRotate] = usePlayer();
    const [stage, setStage, rowsCleared] = useStage(player, resetPlayer);
    const [score, setScore, rows, setRows, level, setlevel] = useStats(rowsCleared);

    const movePlayer = dir => {
        // check x-1, x+1
        if(!checkCollision(player, stage, { x: dir, y: 0 })) {
            updatePlyerPos({ x: dir, y: 0 });
        }
    }

    const startGame = () => {
        // Reset stage, player, dropTime
        setDropTime(1000);
        setStage(createStage());
        resetPlayer();
        setGameOver(false);
    }

    const drop = () => {
        if(!checkCollision(player, stage, { x: 0, y: 1})) {
            //다음 y좌표가 이동이 가능하면 블럭 한칸 내림
            updatePlyerPos({ x: 0, y: 1, collide: false })
        } else { 
            //Game Over
            //다음 y좌표 이동 불가 + 현재 죄표가 1보다 작다면? Game Over
            if(player.pos.y < 1) {
                console.log('Game Over!!!');
                setGameOver(true);
                setDropTime(null);
            }
            updatePlyerPos({x: 0, y: 0, collide: true});
        }
    }

    const keyUp = ({ keyCode }) => {
        if(!gameOver) {
            if(keyCode === 40) {
                setDropTime(1000);
            }
        }
    }

    const dropPlayer = () => {
        setDropTime(null);
        drop();
    }

    const move = ({ keyCode }) => {
        if(!gameOver) {
            if(keyCode === 37) {
                movePlayer(-1);
            } else if (keyCode === 39) {
                movePlayer(1);
            } else if (keyCode === 40) {
                dropPlayer();
            } else if (keyCode === 38) {
                playerRotate(stage, 1);
            }
        }
    }

    useInterval(() => {
        drop();
    }, dropTime);

    return( 
        <StyledTetrisWrapper role='button' tabIndex='0' onKeyDown={e => move(e)} onKeyUp={keyUp}>
            <StyledTetris>
                <Stage stage={ stage } />
                <aside>
                    {gameOver ? (<Display gameOver={gameOver} text="Game Over" />) : (
                    <div>
                        <Display text="Score" stat={score} />
                        <Display text="Rows" stat={rows} />
                        <Display text="Level" stat={level} />
                    </div>
                    )}
                    <StartButton callback={startGame}/>
                </aside>
            </StyledTetris>
        </StyledTetrisWrapper>
    );
}

export default Tetris;
