import window98 from "../img/window98.png";
import "../css/Footer.css";
import internet from "../img/internet.png";
import { useClock } from "../hooks/useClock";

const Footer = () => {
    //현재 시간 가져오기
    const time = useClock();
    //onClick
    const onClickInternet = () => {
        window.open('https://www.google.com', '_blank');
    };


    return(
        <div className="footer">

            {/* 시작버튼 */}
            <button className="window">
                <div className="start-button-highlight"></div>
                <img src={window98} alt="window98 img" className="window98" />
                <span className="start">시작</span>
            </button>

            {/* 시작버튼 옆 아이콘 모음 및 가상 bar */}
            <div className="tools">
                <div className="vertical-line"></div>
                <div className="vertical-bar"></div>

                {/* icons */}
                <div className="shortcut-icon-container">
                    <button onClick={onClickInternet} className="icon-button">
                        <img src={internet} alt="internet img" className="internet footer-icon"></img>
                    </button>
                </div>

                <div className="vertical-line"></div>
                <div className="vertical-bar"></div>
            </div>

            {/* 우측 시간표시기 */}
            <div className="time-container">
                <div className="icon-container2">
                    <div>달력</div>
                    <div>sound</div>
                </div>

                <div className="time">{time}</div>
            </div>

        </div>
    );
}
export default Footer;
