import "../css/Game.css";
import Help from "./Help";
import Tetris from "./Tetris/Tetris";
import TetrisHeader from "./TetrisHeader";
import { useState } from "react";


const Game = () => {
    const [isStart, setIsStart] = useState(false);
    const onClickSingle = () => {
        if(isStart === false){
            setIsStart(true);
        }else{
            setIsStart(false);
        }
    }

    return(
        <div className="Game">
            <TetrisHeader />
            {isStart ? <Tetris setIsStart ={onClickSingle}/> : 
            (<div id="tetris">
                <h1>TETRIS</h1>
                <Help />
                <div className="button-list">
                    <button onClick={onClickSingle}><span>{'>'}</span>Single Play</button>
                    <button><span>{'>'}</span>Multi Play</button>
                    <button><span>{'>'}</span>Setting</button>
                </div>
            </div>)}
        </div>
    )
}
export default Game;
