import { useState, useEffect } from "react";

import { checkCollision, createStage } from "../../gameHelpers";

export const useStage = (player, resetPlayer) => {
    const [stage, setStage] = useState(createStage());
    const [rowsCleared, setRowsCleared] = useState(0);    
    const [ghostPos, setGhostPos] = useState({x: 0, y: 0});


    useEffect( () => {
        setRowsCleared(0);

        const sweepRows = newStage => 
            newStage.reduce((ack, row) => {
                if(row.findIndex(cell => cell[0] === 0) ===-1) {
                    setRowsCleared(prev => prev + 1);
                    ack.unshift(new Array(newStage[0].length).fill([0, 'clear']));
                    return ack;
                }
                ack.push(row);
                return ack;
            }, []);

        const calculateGhostPos = (stage) => {
            const ghostPos = [];
            const clonedPlayer = JSON.parse(JSON.stringify(player));

            while(!checkCollision(clonedPlayer, stage, {x: 0, y: 1})) {
                console.log('...')
                clonedPlayer.pos.y += 1;
            }
            const calculatedPos = {...clonedPlayer.pos};

            clonedPlayer.tetromino.forEach((row, y) => {
                row.forEach((value, x) => {
                    if(value !== 0) {
                        ghostPos.push({x: y + calculateGhostPos.y, y: x + calculateGhostPos.x});
                    }
                })
            })            

            return calculatedPos;
        }

        const updateStage = prevStage => {
            const newStage = prevStage.map(row => row.map(cell => (cell[1] === 'clear' ? [0, 'clear'] : cell)));

            player.tetromino.forEach((row, y) => {
                row.forEach((value, x) => {
                    if(value !== 0) {
                        newStage[y + player.pos.y][x + player.pos.x] = [value, `${player.collide ? 'merged' : 'clear'}`];
                    }
                })
            });


            //충돌 감지시 새 블럭 생성
            if(player.collide) {  
                resetPlayer() 
                return sweepRows(newStage);
            }
            return newStage;
        }
        // Ghost Position 
        setGhostPos(calculateGhostPos(stage));
        // Update Block Position as Stage State
        setStage(prev => updateStage(prev));

    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [player, resetPlayer]);

    return [stage, setStage, rowsCleared, ghostPos];
}
