import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { setIsStart } from "../store/IsStart";

export const useCountDown = (isClick) => {
    const [count, setCount] = useState(3);
    const dispatch = useDispatch();

    useEffect( () => {
        let timer;
        if (isClick && count > 0) {
            timer = setInterval(() => {
            setCount((prevCount) => prevCount - 1);
            }, 1000);
        }
        if(count === 0) {
            console.log('count 0')
        }

        return () => {
            clearInterval(timer);
        };
    }, [isClick, count]);

    return count;
};
