import { useState, useEffect } from "react";

export const useCountDown = (isClick) => {
    const [count, setCount] = useState(3);
    const [isStart, setIsStart] = useState(false);
    useEffect( () => {
        let timer;
        if (isClick && count > 0) {
            timer = setInterval(() => {
            setCount((prevCount) => prevCount - 1);
            }, 1000);
        }
        return () => {
            clearInterval(timer);
        };
    }, [isClick, count]);

    return [count, true];
};
